import {createStore} from 'redux';
import {createSlice, configureStore} from '@reduxjs/toolkit'

const initialState = {
    counter:0,
    showCounter:true
}

const counterSlice = createSlice({
    name:'counter',
    initialState: initialState,
    reducers: {
       increment(state) {
        state.counter++;
       },
       decrement(state) {
        state.counter--;
       },
       increase(state, action) {
        state.counter = state.counter + action.payload; // payload is a special name for the extra data that is passed with the action
       },
       toggle(state) {
        state.showCounter = !state.showCounter;
       },
    }
})

// configureStore combines createStore and combineReducers, so we can have multiple reducers
const store = configureStore({
    reducer: counterSlice.reducer
});


export const counterActions = counterSlice.actions;
export default store;
